window.openBusinessCaseModal=function(){const overlay=document.getElementById("rtbcbModalOverlay");if(overlay){overlay.classList.add("active");document.body.style.overflow="hidden";setTimeout(()=>{if(window.businessCaseBuilder){window.businessCaseBuilder.reinitialize()}else{window.businessCaseBuilder=new BusinessCaseBuilder}},100)}};window.closeBusinessCaseModal=function(){const overlay=document.getElementById("rtbcbModalOverlay");if(overlay){overlay.classList.remove("active");document.body.style.overflow=""}};document.addEventListener("DOMContentLoaded",function(){try{if(document.getElementById("rtbcbForm")){window.businessCaseBuilder=new BusinessCaseBuilder}}catch(error){console.error("BusinessCaseBuilder initialization failed:",error)}});class BusinessCaseBuilder{constructor(){this.currentStep=1;this.totalSteps=5;this.form=document.getElementById("rtbcbForm");this.overlay=document.getElementById("rtbcbModalOverlay");if(!this.form)return;this.init()}init(){this.cacheElements();this.bindEvents();this.updateStepVisibility();this.updateProgressIndicator()}cacheElements(){this.nextBtn=this.form.querySelector(".rtbcb-nav-next");this.prevBtn=this.form.querySelector(".rtbcb-nav-prev");this.submitBtn=this.form.querySelector(".rtbcb-nav-submit");this.steps=this.form.querySelectorAll(".rtbcb-wizard-step");this.progressSteps=this.form.querySelectorAll(".rtbcb-progress-step");this.stepFields={1:["company_name","company_size"],2:["hours_reconciliation","hours_cash_positioning","num_banks","ftes"],3:["pain_points"],4:["business_objective","implementation_timeline","budget_range"],5:["email"]}}bindEvents(){if(this.nextBtn){this.nextBtn.addEventListener("click",e=>{e.preventDefault();this.handleNext()})}if(this.prevBtn){this.prevBtn.addEventListener("click",e=>{e.preventDefault();this.handlePrev()})}this.form.addEventListener("submit",e=>{e.preventDefault();if(this.validateStep(this.totalSteps)){this.handleSubmit()}});const painPointCards=this.form.querySelectorAll(".rtbcb-pain-point-card");painPointCards.forEach(card=>{const checkbox=card.querySelector('input[type="checkbox"]');if(checkbox){checkbox.addEventListener("change",()=>{card.classList.toggle("rtbcb-selected",checkbox.checked)})}});this.form.querySelectorAll("input, select").forEach(field=>{field.addEventListener("blur",()=>this.validateField(field));field.addEventListener("input",()=>this.clearFieldError(field))});document.addEventListener("keydown",e=>{if(e.key==="Escape"&&this.overlay.classList.contains("active")){window.closeBusinessCaseModal()}})}handleNext(){if(this.validateStep(this.currentStep)){if(this.currentStep<this.totalSteps){this.currentStep++;this.updateStepVisibility();this.updateProgressIndicator();this.scrollToTop()}}}handlePrev(){if(this.currentStep>1){this.currentStep--;this.updateStepVisibility();this.updateProgressIndicator();this.scrollToTop()}}validateStep(stepNumber){let isValid=true;const fieldsToValidate=this.stepFields[stepNumber];if(stepNumber===3){const checkedBoxes=this.form.querySelectorAll('input[name="pain_points[]"]:checked');if(checkedBoxes.length===0){this.showStepError(3,"Please select at least one challenge");return false}}else{fieldsToValidate.forEach(fieldName=>{const field=this.form.querySelector(`[name="${fieldName}"]`);if(field&&!this.validateField(field)){isValid=false}})}return isValid}showFieldWarning(field,message){const fieldContainer=field.closest(".rtbcb-field");if(!fieldContainer)return;const existingWarning=fieldContainer.querySelector(".rtbcb-field-warning");if(existingWarning){existingWarning.remove()}const warningEl=document.createElement("div");warningEl.className="rtbcb-field-warning";warningEl.textContent=message;warningEl.style.color="#f59e0b";warningEl.style.fontSize="12px";warningEl.style.marginTop="4px";fieldContainer.appendChild(warningEl)}validateField(field){const value=field.value.trim();let isValid=true;let errorMessage="";if(field.hasAttribute("required")&&!value){errorMessage="This field is required";isValid=false}if(field.name==="company_name"&&value){if(value.length<2){errorMessage="Company name must be at least 2 characters";isValid=false}else if(value.length>100){errorMessage="Company name must be less than 100 characters";isValid=false}else if(/^[^a-zA-Z]*$/.test(value)){errorMessage="Please enter a valid company name";isValid=false}}if(field.type==="email"&&value){const emailRegex=/^[^\s@]+@[^\s@]+\.[^\s@]+$/;if(!emailRegex.test(value)){errorMessage="Please enter a valid email address";isValid=false}const domain=value.split("@")[1]?.toLowerCase();const consumerDomains=["gmail.com","yahoo.com","hotmail.com","outlook.com","aol.com"];if(domain&&consumerDomains.includes(domain)){this.showFieldWarning(field,"Consider using your business email for better results")}}if(field.type==="number"&&value){const num=parseFloat(value);const min=parseFloat(field.min);const max=parseFloat(field.max);if(isNaN(num)){errorMessage="Please enter a valid number";isValid=false}else if(min!==undefined&&num<min){errorMessage=`Value must be at least ${min}`;isValid=false}else if(max!==undefined&&num>max){errorMessage=`Value must not exceed ${max}`;isValid=false}}if(!isValid){this.showFieldError(field,errorMessage)}else{this.clearFieldError(field)}return isValid}showFieldError(field,message){const fieldContainer=field.closest(".rtbcb-field");if(!fieldContainer)return;this.clearFieldError(field);field.classList.add("rtbcb-field-invalid");const errorEl=document.createElement("div");errorEl.className="rtbcb-field-error";errorEl.textContent=message;fieldContainer.appendChild(errorEl)}clearFieldError(field){const fieldContainer=field.closest(".rtbcb-field");if(!fieldContainer)return;field.classList.remove("rtbcb-field-invalid");const existingError=fieldContainer.querySelector(".rtbcb-field-error");if(existingError){existingError.remove()}}showStepError(stepNumber,message){const step=this.steps[stepNumber-1];if(!step)return;const validationDiv=step.querySelector(".rtbcb-pain-points-validation");if(validationDiv){const messageDiv=validationDiv.querySelector(".rtbcb-validation-message");if(messageDiv){messageDiv.textContent=message;messageDiv.style.display="block";messageDiv.style.color="#ef4444";setTimeout(()=>{messageDiv.style.display="none"},5e3)}}}updateStepVisibility(){this.steps.forEach((step,index)=>{const stepNum=index+1;if(stepNum===this.currentStep){step.classList.add("active");step.style.display="block"}else{step.classList.remove("active");step.style.display="none"}});if(this.prevBtn){this.prevBtn.style.display=this.currentStep===1?"none":"inline-flex"}if(this.nextBtn){this.nextBtn.style.display=this.currentStep===this.totalSteps?"none":"inline-flex"}if(this.submitBtn){this.submitBtn.style.display=this.currentStep===this.totalSteps?"inline-flex":"none"}const fallbackSubmit=this.form.querySelector(".rtbcb-fallback-submit");if(fallbackSubmit){fallbackSubmit.style.display="none"}}updateProgressIndicator(){this.progressSteps.forEach((step,index)=>{const stepNum=index+1;if(stepNum<this.currentStep){step.classList.add("completed");step.classList.remove("active")}else if(stepNum===this.currentStep){step.classList.add("active");step.classList.remove("completed")}else{step.classList.remove("active","completed")}})}scrollToTop(){const modalBody=this.form.closest(".rtbcb-modal-body");if(modalBody){modalBody.scrollTop=0}}async handleSubmit(){this.showProgress();try{const formData=new FormData(this.form);formData.append("action","rtbcb_generate_case");const response=await fetch(ajaxObj.ajax_url,{method:"POST",body:formData});if(!response.ok){throw new Error(`Server error: ${response.status}`)}const result=await response.json();if(result.success){this.showResults(result.data)}else{throw new Error(result.data?.message||"Failed to generate business case")}}catch(error){console.error("Submission error:",error);this.showError(error.message)}}showProgress(){const formContainer=this.form.closest(".rtbcb-form-container");if(formContainer){formContainer.style.display="none"}const progressHTML=`\n            <div class="rtbcb-progress-overlay" style="position: relative; background: none;">\n                <div class="rtbcb-progress-content">\n                    <div class="rtbcb-progress-spinner"></div>\n                    <div class="rtbcb-progress-text">Generating Your Business Case</div>\n                    <div class="rtbcb-progress-step">\n                        <span class="rtbcb-progress-step-text">Analyzing your treasury operations...</span>\n                    </div>\n                </div>\n            </div>\n        `;const modalBody=this.form.closest(".rtbcb-modal-body");if(modalBody){modalBody.insertAdjacentHTML("beforeend",progressHTML)}this.animateProgressSteps()}animateProgressSteps(){const companyName=this.form.querySelector('[name="company_name"]')?.value||"your company";const steps=[`Analyzing ${companyName}'s treasury operations...`,`Calculating ROI projections for ${companyName}...`,`Generating personalized recommendations...`,`Preparing ${companyName}'s business case report...`];let currentStep=0;const stepElement=document.querySelector(".rtbcb-progress-step-text");if(stepElement){this.progressInterval=setInterval(()=>{currentStep=(currentStep+1)%steps.length;stepElement.textContent=steps[currentStep];stepElement.parentElement.classList.add("rtbcb-progress-step-animation");setTimeout(()=>{stepElement.parentElement.classList.remove("rtbcb-progress-step-animation")},500)},2500)}}showResults(data){if(this.progressInterval){clearInterval(this.progressInterval)}window.closeBusinessCaseModal();const resultsContainer=document.getElementById("rtbcbResults");if(resultsContainer){resultsContainer.innerHTML=this.renderResults(data);this.populateRiskAssessment(data.narrative?.risks||[]);resultsContainer.style.display="block";resultsContainer.scrollIntoView({behavior:"smooth",block:"start"})}}renderResults(data){const{scenarios:scenarios,recommendation:recommendation,narrative:narrative,company_name:company_name}=data;const displayName=company_name||"Your Company";return`\n            <div class="rtbcb-results-container">\n                <div class="rtbcb-results-header">\n                    <div class="rtbcb-results-badge">\n                        <span class="rtbcb-badge-icon">✓</span>\n                        Business Case Generated Successfully\n                    </div>\n                    <h2>${displayName} Treasury Technology Business Case</h2>\n                    <p class="rtbcb-results-subtitle">Personalized ROI analysis and strategic recommendations</p>\n                </div>\n\n                ${this.renderRecommendation(recommendation,displayName)}\n                ${this.renderROISummary(scenarios,displayName)}\n                ${this.renderNarrative(narrative,displayName)}\n                ${this.renderRiskAssessmentSection()}\n                ${this.renderNextSteps(narrative.next_actions||[],displayName)}\n                ${this.renderActions(data)}\n            </div>\n        `}renderRecommendation(recommendation,companyName){const category=recommendation.category_info||{};const confidence=Math.round((recommendation.confidence||.75)*100);return`\n            <div class="rtbcb-recommendation-card">\n                <div class="rtbcb-recommendation-header">\n                    <h3>Recommended Solution for ${companyName}</h3>\n                    <span class="rtbcb-confidence-badge">${confidence}% Confidence</span>\n                </div>\n                <div class="rtbcb-recommendation-name">${category.name||"Treasury Management System"}</div>\n                <div class="rtbcb-recommendation-description">\n                    ${category.description||"Modern treasury platform with automation and analytics"}\n                </div>\n                <div class="rtbcb-recommendation-reasoning">\n                    ${recommendation.reasoning||`Based on ${companyName}'s profile, this solution best fits your needs.`}\n                </div>\n            </div>\n        `}renderROISummary(scenarios,companyName){return`\n            <div class="rtbcb-roi-section">\n                <h3>${companyName} Projected Annual Benefits</h3>\n                <div class="rtbcb-roi-summary">\n                    <div class="rtbcb-scenario">\n                        <div class="rtbcb-scenario-label">Conservative</div>\n                        <div class="rtbcb-scenario-amount">$${this.formatNumber(scenarios.low?.total_annual_benefit||0)}</div>\n                        <div class="rtbcb-scenario-confidence">80% probability</div>\n                    </div>\n                    <div class="rtbcb-scenario rtbcb-scenario-base">\n                        <div class="rtbcb-scenario-label">Base Case</div>\n                        <div class="rtbcb-scenario-amount">$${this.formatNumber(scenarios.base?.total_annual_benefit||0)}</div>\n                        <div class="rtbcb-scenario-confidence">Most likely outcome</div>\n                    </div>\n                    <div class="rtbcb-scenario">\n                        <div class="rtbcb-scenario-label">Optimistic</div>\n                        <div class="rtbcb-scenario-amount">$${this.formatNumber(scenarios.high?.total_annual_benefit||0)}</div>\n                        <div class="rtbcb-scenario-confidence">Best case scenario</div>\n                    </div>\n                </div>\n                ${this.renderBenefitBreakdown(scenarios.base)}\n            </div>\n        `}renderBenefitBreakdown(scenario){if(!scenario)return"";const benefits=[{label:"Labor Savings",amount:scenario.labor_savings||0},{label:"Fee Reduction",amount:scenario.fee_savings||0},{label:"Error Prevention",amount:scenario.error_reduction||0}];const total=scenario.total_annual_benefit||0;return`\n            <div class="rtbcb-benefit-breakdown">\n                <h4>Benefit Breakdown</h4>\n                <div class="rtbcb-chart-fallback">\n                    <div class="rtbcb-benefit-bars">\n                        ${benefits.map(b=>{const percentage=total>0?(b.amount/total*100).toFixed(0):0;return`\n                                <div class="rtbcb-benefit-bar">\n                                    <div class="rtbcb-benefit-bar-label">${b.label}</div>\n                                    <div class="rtbcb-benefit-bar-fill" style="width: ${percentage}%; background: linear-gradient(90deg, #7216f4, #8f47f6);">\n                                        $${this.formatNumber(b.amount)}\n                                    </div>\n                                </div>\n                            `}).join("")}\n                    </div>\n                </div>\n            </div>\n        `}renderNarrative(narrative){return`\n            <div class="rtbcb-narrative-section">\n                <h3>Executive Summary</h3>\n                <div class="rtbcb-narrative-content">\n                    ${narrative.narrative||"Treasury technology investment presents a compelling opportunity for operational efficiency."}\n                </div>\n            </div>\n        `}renderRiskAssessmentSection(){return`\n            <div class="rtbcb-risk-assessment">\n                <h3>Risk Assessment</h3>\n                <ul class="rtbcb-risk-list"></ul>\n            </div>\n        `}populateRiskAssessment(risks){const list=document.querySelector(".rtbcb-risk-list");if(!list)return;list.innerHTML="";(risks||[]).forEach(risk=>{const item=document.createElement("li");if(risk&&typeof risk==="object"){const values=Object.values(risk).filter(Boolean);item.textContent=values.join(" - ")}else if(risk!=null){item.textContent=String(risk)}list.appendChild(item)})}renderNextSteps(steps){if(!steps||steps.length===0){steps=["Present business case to stakeholders","Evaluate solution providers","Develop implementation timeline","Plan change management strategy"]}return`\n            <div class="rtbcb-next-steps">\n                <h3>Recommended Next Steps</h3>\n                <div class="rtbcb-steps-grid">\n                    ${steps.map((step,index)=>`\n                        <div class="rtbcb-step">\n                            <div class="rtbcb-step-number">${index+1}</div>\n                            <div class="rtbcb-step-content">\n                                <p>${step}</p>\n                            </div>\n                        </div>\n                    `).join("")}\n                </div>\n            </div>\n        `}renderActions(data){return`\n            <div class="rtbcb-actions-section">\n                ${data.download_url?`\n                    <a href="${data.download_url}" class="rtbcb-action-btn rtbcb-btn-primary" download>\n                        <span class="rtbcb-btn-icon">📄</span>\n                        Download PDF Report\n                    </a>\n                `:""}\n                <button type="button" class="rtbcb-action-btn rtbcb-btn-secondary" onclick="window.print()">\n                    <span class="rtbcb-btn-icon">🖨️</span>\n                    Print Results\n                </button>\n                <button type="button" class="rtbcb-action-btn rtbcb-btn-secondary" onclick="location.reload()">\n                    <span class="rtbcb-btn-icon">🔄</span>\n                    Start Over\n                </button>\n            </div>\n        `}showError(message){const progressOverlay=document.querySelector(".rtbcb-progress-overlay");if(progressOverlay){progressOverlay.remove()}const modalBody=this.form.closest(".rtbcb-modal-body");if(modalBody){const errorHTML=`\n                <div class="rtbcb-error-container" style="padding: 40px; text-align: center;">\n                    <div class="rtbcb-error-icon" style="font-size: 48px; color: #ef4444; margin-bottom: 20px;">⚠️</div>\n                    <h3 style="color: #ef4444; margin-bottom: 16px;">Unable to Generate Business Case</h3>\n                    <p style="color: #6b7280; margin-bottom: 24px;">${message}</p>\n                    <button type="button" class="rtbcb-action-btn rtbcb-btn-primary" onclick="location.reload()">\n                        Try Again\n                    </button>\n                </div>\n            `;modalBody.innerHTML=errorHTML}}formatNumber(num){return new Intl.NumberFormat("en-US").format(Math.round(num))}reinitialize(){this.currentStep=1;this.updateStepVisibility();this.updateProgressIndicator()}}